        public static void tblInventoryLifeCreate()
        {
            int rowNum;
            int count;
            string tbl_Name;

            SQL_Add.ExecQuery("SELECT * FROM dtbInventoryVerse");
            count = Convert.ToInt32(SQL_Add.RecordCount) - 1;
            rowNum = Convert.ToInt32(SQL_Add.DBDT.Rows[count][0]);
            tbl_Name = "dtbInventoryLife" + rowNum;

            SQL.ExecQuery("CREATE TABLE " + tbl_Name + "(" +
                "Prime INT IDENTITY(1,1) NOT NULL PRIMARY KEY, " +
                "[Life Cycle] VARCHAR(50), " +
                "small_tick_num INT, " +
                "large_tick_num INT, " +
                "Distribution DECIMAL(7,4), " +
                "[Low Range] VARCHAR(50), " +
                "[High Range] VARCHAR(50), " +
                ");");

            // PAYOR MIX CATEGORIES
            SQL.ExecQuery("INSERT INTO " + tbl_Name + " ([Life Cycle])" +
                "VALUES ('Series')," +
                "('Facility Stage');");
        }

